          <a-descriptions-item label="年龄">{{ thisFullUserInfo.age }}</a-descriptions-item>
          <a-descriptions-item label="性别">{{ thisFullUserInfo.gender }}</a-descriptions-item>
          <a-descriptions-item label="学工号">{{ thisFullUserInfo.user_id }}</a-descriptions-item>
